import React,{useCallback,useReducer} from 'react';
import './NewPlace.css';
import Input from '../../shared/components/FormElements/Input';
import Button from '/home/abhinav/Desktop/Live-Photo sharer/src/shared/components/FormElements/Button.js';
import {VALIDATOR_REQUIRE,VALIDATOR_MINLENGTH} from '/home/abhinav/Desktop/Live-Photo sharer/src/util/validators.js';


const formReducer = (state,action) =>{
  switch (action.type){
    case 'INPUT_CHANGE':
    let formIsvalid = true;
    for(const inputId in state.inputs){
      if(inputId === action.inputId){
        formIsvalid = formIsvalid && action.isValid;
      }
      else{
        formIsvalid = formIsvalid && state.inputs[inputId].isValid;
      }
    }
    return {
      ...state,
      input: {
        ...state.inputs,
        [action.inputId]:{value:action.value,isValid:action.isValid}
      },
      isValid: formIsvalid
    };
    default:
    return state;
  }
};
const NewPlace = () => {

const [formState,dispatch] = useReducer(formReducer,{
  inputs:{
    title:{
      value:'',
      isValid: false

  },
  description:{
    value:'',
    isValid: false
  }
},
  isValid: false
});

  const inputHandler = useCallback((id,value,isValid) =>{
    dispatch({type:'INPUT_CHANGE',value:value,isValid:isValid,inputId:id});
  },[]);
  const descriptionInputHandler = useCallback((id,value,isValid) =>{},[]);

  return(
  <form className="place-form">
  <Input
  id = "title"
  element="input"
  type="text"
  label="Title"
  validators={[VALIDATOR_REQUIRE()]}
  errorText="Please enter valid text."
  onInput={inputHandler}
  />
  <Input
  id="description"
  element="textarea"
  type="text"
  label="Description"
  validators={[VALIDATOR_REQUIRE(),VALIDATOR_MINLENGTH(5)]}
  errorText="Please enter valid text.(atleast char)"
  onInput={inputHandler}
  />
  <Button />
  </form>
);
};

export default NewPlace;
